import { makeRequest } from "./makeRequest";

const URL = "/users/me";

export const getUser = (userId, config) =>
  makeRequest({
    method: "GET",
    url: `${URL}`,
    ...config,
  });

export const mutateUser = (config) => {
  config.url = `${URL}${config.url}`;
  return makeRequest({
    method: "PUT",
    ...config,
  });
};
