import axios from "axios";
import { getError } from "../utils";

const API_ENDPOINT = "http://localhost:3000";

export const makeRequest = (config) => {
  config.url = `${API_ENDPOINT}${config.url}`;

  return axios(config).catch((err) => useGetError(err));
};
