import { getUserById } from "../../api/users";
import "./styles.css";

const Comment = ( { nickname, text, author }) => {
  let user = ''
  getUserById(author).then(data => user = data.name)

  return (
    <div className="cnCommentRoot">
      <span className="cnCommentName">{user}:</span>
      <span>{text}</span>
    </div>
  );
};

export default Comment;
