import { useState } from "react"

export const useSorter = (data, tab) => {
    let dataArr = [].concat(data)
    dataArr.sort((a, b) => {
        switch (tab) {

            case "up":
                return b.author.nickname - a.author.nickname
            case "down":
                return a.author.nickname - b.author.nickname
            default: 
                return a.author.nickname - b.author.nickname
        }
    }).map(({ author, imgUrl, id, likes, comments }) => (
            <DetailedCard
              key={id}
              id={id}
              userName={author.nickname}
              avatarUrl={author.avatarUrl}
              userId={author.id}
              imgUrl={imgUrl}
              likes={likes.length}
              isLikedByYou={likes.includes(authorizedUser.id)}
              comments={comments}
              className="cnMainPageCard"
              onLikeClick={onLikeClick}
              onCommentSendClick={onCommentSendClick}
              mutateLoading={mutateLoading}
            />
          ))
}
