import { useState } from "react"

export const sorter = (data, tab) => {
    let dataArr = [].concat(data)
    dataArr.sort((a, b) => {
        switch (tab) {

            case "up":
                return b.author.nickname - a.author.nickname
            case "down":
                return a.author.nickname - b.author.nickname
            default: 
                return a.author.nickname - b.author.nickname
        }
    })
}
